myIDs = [int(rowcol[:-3], 2) * 8 + int(rowcol[-3:], 2) for rowcol in [''.join(['1' if (c == "B" or c == "R") else '0' for c in l.rstrip()]) for l in open("input.txt", "r").readlines()]]
print(int([row * 8 + col for col in range(8) for row in range(1, 128 - 1) if row * 8 + col not in myIDs and row * 8 + col - 1 in myIDs and row * 8 + col + 1 in myIDs]))